import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const ShopContext = createContext();

export const ShopProvider = ({ children }) => {
    const [cart, setCart] = useState(new Map());
    const [user, setUser] = useState(null); // Assume you have user context or state

    // Function to fetch the cart from the backend
    const fetchCart = async (userId) => {
        try {
            const response = await axios.get(`/api/cart?userId=${userId}`);
            setCart(new Map(Object.entries(response.data))); // Convert response to a Map
        } catch (error) {
            console.error('Error fetching cart:', error);
        }
    };

    // Function to add an item to the cart
    const addToCart = async (productId, quantity = 1) => {
        if (!user) {
            console.error('User not logged in');
            return;
        }

        try {
            const userId = user.id;
            await axios.post('/api/cart', { userId, productId, quantity });
            fetchCart(userId); // Refresh the cart
        } catch (error) {
            console.error('Error adding to cart:', error);
        }
    };

    // Function to remove an item from the cart
    const removeFromCart = async (productId) => {
        if (!user) {
            console.error('User not logged in');
            return;
        }

        try {
            const userId = user.id;
            await axios.delete(`/api/cart/${productId}`, { data: { userId } });
            fetchCart(userId); // Refresh the cart
        } catch (error) {
            console.error('Error removing from cart:', error);
        }
    };

    // Fetch the cart when the user logs in
    useEffect(() => {
        if (user) {
            fetchCart(user.id);
        }
    }, [user]);

    return (
        <ShopContext.Provider value={{ cart, addToCart, removeFromCart, setUser }}>
            {children}
        </ShopContext.Provider>
    );
};
